      {% set dummy = tmp_list.append(item["@id"]) %}
      {% set dummy1 = rec_dict.update({item.destinationIp: tmp_list}) %}
    {% else %}
      {% set tmp_list = [] %}
      {% set dummy = tmp_list.append(item["@id"]) %}
      {% set dummy1 = rec_dict.update({item.destinationIp: tmp_list}) %}
      {% set dummy1 = ip_cname.update({item.destinationIp: item.computerName}) %}

    {% endif %}
  {% endif %}
{% endfor %}


{% for ip, alert_records in rec_dict.items() %}
